// Helper functions

func statusClass(status store.TaskExecutionStatus) string {
	switch status {
	case store.StatusSucceeded:
		return "is-success"
	case store.StatusFailed:
		return "is-danger"
	case store.StatusRunning, store.StatusContainerStarted:
		return "is-info"
	case store.StatusPending:
		return "is-warning"
	default:
		return ""
	}
}

func statusIcon(status store.TaskExecutionStatus) string {
	switch status {
	case store.StatusSucceeded:
		return "fas fa-check"
	case store.StatusFailed:
		return "fas fa-times"
	case store.StatusRunning, store.StatusContainerStarted:
		return "fas fa-spin"
	case store.StatusPending:
		return "fas fa-clock"
	default:
		return "fas fa-ellipsis-h"
	}
}

func getFileTypeIcon(mimeType string) string {
	switch {
	case strings.HasPrefix(mimeType, "image/"):
		return "fa-image"
	case strings.HasPrefix(mimeType, "text/"):
		return "fa-file-alt"
	case strings.Contains(mimeType, "pdf"):
		return "fa-file-pdf"
	case strings.Contains(mimeType, "json"):
		return "fa-file-code"
	case strings.Contains(mimeType, "zip") || strings.Contains(mimeType, "archive"):
		return "fa-file-archive"
	default:
		return "fa-file"
	}
}

func getFileTypeColor(mimeType string) string {
	switch {
	case strings.HasPrefix(mimeType, "image/"):
		return "info"
	case strings.HasPrefix(mimeType, "text/"):
		return "primary"
	case strings.Contains(mimeType, "pdf"):
		return "danger"
	case strings.Contains(mimeType, "json"):
		return "success"
	default:
		return "grey"
	}
}

func formatFileSize(bytes int64) string {
	const unit = 1024
	if bytes < unit {
		return fmt.Sprintf("%d B", bytes)
	}
	div, exp := int64(unit), 0
	for n := bytes / unit; n >= unit; n /= unit {
		div *= unit
		exp++
	}
	return fmt.Sprintf("%.1f %cB", float64(bytes)/float64(div), "KMGTPE"[exp])
}

